WS = reflect(-lDirWS, nDirWS);//获取光视反方向 //采样贴图 float4 var_MainOpTex = tex2D(_MainOpTex, i.uv0); float4 var_MSEATex = tex2D(_MSEATex, i.uv0); float4 var_EDTTex = tex2D(_EDTTex, i.uv0); float4 var_DetalTex = tex2D(_DetalTex, i.uv0); // 准备点积结果 float ndotl = dot(nDirWS, lDirWS); //得到lambert float vdotr = dot(vDirWS, lrDirWS); //得到phong float vdotn = dot(vDirWS, nDirWS); //得到视方向，为fresnel准备 //编写光照模型 float lambert = max(0.0, ndotl); float Phong = pow(max(0.0,vdotr),_SpecPow); //Tint mask float3 var_Tintmask = _ColTint * var_EDTTex.b; // float4 Use_ColorTintMask = UNITY_ACCESS_INSTANCED_PROP( Props, _Use_ColorTint_Mask ); float3 LambertPhong = ((var_MainOpTex* (lambert*0.5+0.5)) )+ (_highCol * (Phong * (var_MSEATex.g * var_MSEATex.b))); //shadow float shadow = LIGHT_ATTENUATION (i) ; // 同样Unity封装好的函数 可取出投影 float var_shadowPow = pow( shadow , _shadowPow) ; //阴影硬度 float3 var_shadowCol = (_shadowCol.rgb * (1.0 - var_shadowPow)) + var_shadowPow; float3 Lightshadow = LambertPhong * var_shadowCol ; //3ColAmbient 引用库文件 // float3 envCol = TriColAmbient (nDirWS, _EnvUpCol, _EnvSideCol, _EnvDownCol); float uMask = max(0.0, nDirWS.g); // 获取朝上部分遮罩 float dMask = max(0.0, -nDirWS.g); // 获取朝下部分遮罩 float sMask = 1.0 - uMask - dMask; // 获取侧面部分遮罩 float3 envCol =_EnvUpCol * uMask + _EnvSideCol * sMask + _EnvDownCol * dMask; // 混合环境色 // cubemap float3 var_Cubemap = texCUBElod(_Cubemap, float4(vrDirWS, _CubemapMip)).rgb; float var_fresnel = pow(max(0.0, 1.0 - vdotn), _FresnelPow); //fresnel float3 envSpecLighting = var_Cubemap * var_fresnel * var_MSEATex.r * _EnvSpecInt; // 叠加菲涅尔效果以及环境镜面反射强度 // 3col Mul AO float3 Col3Ao = var_MSEATex.a * (envCol* _EnvDiffInt )* Lightshadow; float3 finalCol = Col3Ao + envSpecLighting + ( _EmitTint * var_EDTTex.r)+(var_DetalTex * var_EDTTex.g ); //返回结果 return float4 (finalCol , 1.0 ); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\",\"ps：最终还是用了默认的贴图写食人魔的shader 然后 高光度，金属度，表现不正常。待与答案对比； 较前写的森海飞霞的shader也没写好，😭。\"]},\"506\":{\"h\":\"森海飞霞的尝试\",\"t\":[\"根据大作业链接中提到的需求-以及官方data2获取的模型和贴图梳理了下\",\"Texoplish 后\",\"shaderforge\"]},\"507\":{\"c\":[\"shader\"]},\"508\":{\"h\":\"unity-shader-12\",\"t\":[\"摘要：答疑 作业分析\"]},\"509\":{\"h\":\"答疑\",\"t\":[\"透贴阴影问题： \",\"计算投影必须要有一个ShadowCastPass，之所以我们之前没写也对，是因为FallBack了Diffuse，Unity会自动调Diffuse这个Shader的ShadowCastPass，但是它是不支持透贴的；\",\"可以FallBask到一个支持AlphaCutout的Shader上，比如：Legacy Shaders/Transparent/Cutout/VertexLit； 需要注意的是，必须包含Shader中透明相关的所有面板属性；\",\"透贴双面问题： \",\"关闭剔除背面，在声明BlendMode的地方加入：Cull Off 即可；\",\"开线性空间做，Gamma空间看不正常的问题： \",\"目前课程内容不会涉及线性空间，Gamma空间；\",\"如果按公共件的要求，Shader应该支持线性和Gamma效果一致；\"]},\"510\":{\"h\":\"作业分析：\"},\"511\":{\"h\":\"文档分析：\",\"t\":[\"Color贴图，透明贴图，法线贴图，自发光贴图，不赘述；\",\"DetailMask特殊效果相关，DiffuseMask罕见使用，不考虑；\",\"Metalness 金属度贴图，仅为金属遮罩，区别于PBR的金属度；\",\"Specular Mask，镜面反射遮罩，可理解为高光强度图；\",\"Rimlight，边缘光效果，对应有RimMask，边缘光强度图；\",\"Base Tint Mask，高光染色遮罩，控制高光颜色从Color贴图混合的程度；\",\"Specular Exponent，高光次幂，不赘述；\"]},\"512\":{\"h\":\"资源分析：\"},\"513\":{\"h\":\"资源优化：\"},\"514\":{\"h\":\"光照模型分析：\"},\"515\":{\"h\":\"VS准备:\"},\"516\":{\"h\":\"光照模型：\"},\"517\":{\"h\":\"1.DiffCol SpecCol\"},\"518\":{\"h\":\"2.Fresnel\"},\"519\":{\"h\":\"3.DirDiff - 主光漫反射\"},\"520\":{\"h\":\"4.DirSpec - 主光镜面反射\"},\"521\":{\"h\":\"5.EnvDiff - 环境漫反射\"},\"522\":{\"h\":\"6.EnvSpec - 环境镜面反射\"},\"523\":{\"h\":\"7.RimLight - 轮廓光\"},\"524\":{\"h\":\"8.Emission - 自发光\"},\"525\":{\"h\":\"最终混合\"},\"526\":{\"h\":\"透明剪切\"},\"527\":{\"h\":\"透明剪切投影修正\"},\"528\":{\"h\":\"开启双面显示\"},\"529\":{\"h\":\"效果完成\"},\"530\":{\"h\":\"Code\",\"t\":[\"::: code-tabs#shell\",\"@tab Data2.shader\",\" Shader \\\"AP01/L12/Dota2\\\" { Properties { [Header(Texture)] _MainTex (\\\"RGB:颜色 A:透贴\\\", 2d) = \\\"white\\\"{} _MaskTex (\\\"R:高光强度 G:边缘光强度 B:高光染色 A:高光次幂\\\", 2d) = \\\"black\\\"{} _NormTex (\\\"RGB:法线贴图\\\", 2d) = \\\"bump\\\"{} _MatelnessMask (\\\"金属度遮罩\\\", 2d) = \\\"black\\\"{} _EmissionMask (\\\"自发光遮罩\\\", 2d) = \\\"black\\\"{} _DiffWarpTex (\\\"颜色Warp图\\\", 2d) = \\\"gray\\\"{} _FresWarpTex (\\\"菲涅尔Warp图\\\", 2d) = \\\"gray\\\"{} _Cubemap (\\\"环境球\\\", cube) = \\\"_Skybox\\\"{} [Header(DirDiff)] _LightCol (\\\"光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) [Header(DirSpec)] _SpecPow (\\\"高光次幂\\\", range(0.0, 99.0)) = 5 _SpecInt (\\\"高光强度\\\", range(0.0, 10.0)) = 5 [Header(EnvDiff)] _EnvCol (\\\"环境光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) [Header(EnvSpec)] _EnvSpecInt (\\\"环境镜面反射强度\\\", range(0.0, 30.0)) = 0.5 [Header(RimLight)] [HDR]_RimCol (\\\"轮廓光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) [Header(Emission)] _EmitInt (\\\"自发光强度\\\", range(0.0, 10.0)) = 1.0 [HideInInspector] _Cutoff (\\\"Alpha cutoff\\\", Range(0,1)) = 0.5 [HideInInspector] _Color (\\\"Main Color\\\", Color) = (1.0, 1.0, 1.0, 1.0) } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } Cull Off CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #include \\\"AutoLight.cginc\\\" #include \\\"Lighting.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform sampler2D _MainTex; uniform sampler2D _MaskTex; uniform sampler2D _NormTex; uniform sampler2D _MatelnessMask; uniform sampler2D _EmissionMask; uniform sampler2D _DiffWarpTex; uniform sampler2D _FresWarpTex; uniform samplerCUBE _Cubemap; // DirDiff uniform half3 _LightCol; // DirSpec uniform half _SpecPow; uniform half _SpecInt; // EnvDiff uniform half3 _EnvCol; // EnvSpec uniform half _EnvSpecInt; // RimLight uniform half3 _RimCol; // Emission uniform half _EmitInt; // Other uniform half _Cutoff; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点信息 Get✔ float2 uv0 : TEXCOORD0; // UV信息 Get✔ float4 normal : NORMAL; // 法线信息 Get✔ float4 tangent : TANGENT; // 切线信息 Get✔ }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 屏幕顶点位置 float2 uv0 : TEXCOORD0; // UV0 float4 posWS : TEXCOORD1; // 世界空间顶点位置 float3 nDirWS : TEXCOORD2; // 世界空间法线方向 float3 tDirWS : TEXCOORD3; // 世界空间切线方向 float3 bDirWS : TEXCOORD4; // 世界空间副切线方向 LIGHTING_COORDS(5,6) // 投影相关 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>CS o.uv0 = v.uv0; // 传递UV o.posWS = mul(unity_ObjectToWorld, v.vertex); // 顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 法线方向 OS>WS o.tDirWS = normalize(mul(unity_ObjectToWorld, float4(v.tangent.xyz, 0.0)).xyz); // 切线方向 OS>WS o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); // 副切线方向 TRANSFER_VERTEX_TO_FRAGMENT(o) // 投影相关 return o; // 返回输出结构 } // 输出结构>>>像素 float4 frag(VertexOutput i) : COLOR { // 向量准备 half3 nDirTS = UnpackNormal(tex2D(_NormTex, i.uv0)); half3x3 TBN = half3x3(i.tDirWS, i.bDirWS, i.nDirWS); half3 nDirWS = normalize(mul(nDirTS, TBN)); half3 vDirWS = normalize(_WorldSpaceCameraPos.xyz - i.posWS); half3 vrDirWS = reflect(-vDirWS, nDirWS); half3 lDirWS = _WorldSpaceLightPos0.xyz; half3 lrDirWS = reflect(-lDirWS, nDirWS); // 中间量准备 half ndotl = dot(nDirWS, lDirWS); half ndotv = dot(nDirWS, vDirWS); half vdotr = dot(vDirWS, lrDirWS); // 采样纹理 half4 var_MainTex = tex2D(_MainTex, i.uv0); half4 var_MaskTex = tex2D(_MaskTex, i.uv0); half var_MatelnessMask = tex2D(_MatelnessMask, i.uv0).r; half var_EmissionMask = tex2D(_EmissionMask, i.uv0).r; half3 var_FresWarpTex = tex2D(_FresWarpTex, ndotv); half3 var_Cubemap = texCUBElod(_Cubemap, float4(vrDirWS, lerp(8.0, 0.0, var_MaskTex.a))).rgb; // 提取信息 half3 baseCol = var_MainTex.rgb; half opacity = var_MainTex.a; half specInt = var_MaskTex.r; half rimInt = var_MaskTex.g; half specTint = var_MaskTex.b; half specPow = var_MaskTex.a; half matellic = var_MatelnessMask; half emitInt = var_EmissionMask; half3 envCube = var_Cubemap; half shadow = LIGHT_ATTENUATION(i); // 光照模型 // 漫反射颜色 镜面反射颜色 half3 diffCol = lerp(baseCol, half3(0.0, 0.0, 0.0), matellic); half3 specCol = lerp(baseCol, half3(0.3, 0.3, 0.3), specTint) * specInt; // 菲涅尔 half3 fresnel = lerp(var_FresWarpTex, 0.0, matellic); half fresnelCol = fresnel.r; // 无实际用途 half fresnelRim = fresnel.g; half fresnelSpec = fresnel.b; // 光源漫反射 half halfLambert = ndotl * 0.5 + 0.5; half3 var_DiffWarpTex = tex2D(_DiffWarpTex, half2(halfLambert, 0.2)); half3 dirDiff = diffCol * var_DiffWarpTex * _LightCol; // 光源镜面反射 half phong = pow(max(0.0, vdotr), specPow * _SpecPow); half spec = phong * max(0.0, ndotl); spec = max(spec, fresnelSpec); spec = spec * _SpecInt; half3 dirSpec = specCol * spec * _LightCol; // 环境漫反射 half3 envDiff = diffCol * _EnvCol; // 环境镜面反射 half reflectInt = max(fresnelSpec, matellic) * specInt; half3 envSpec = specCol * reflectInt * envCube * _EnvSpecInt; // 轮廓光 half3 rimLight = _RimCol * fresnelRim * rimInt * max(0.0, nDirWS.g); // 自发光 half3 emission = diffCol * emitInt * _EmitInt; // 混合 half3 finalRGB = (dirDiff + dirSpec) * shadow + envDiff + envSpec + rimLight + emission; // 透明剪切 clip(opacity - _Cutoff); // 返回值 return float4(finalRGB, 1.0); } ENDCG } } // 声明回退Shader FallBack \\\"Legacy Shaders/Transparent/Cutout/VertexLit\\\" } \",\":::\"]},\"531\":{\"h\":\"开源Shader\",\"t\":[\"根据作业答案复盘下自己那些做的不足\",\"在文档分析和资源分析上 \",\"没有仔细阅读文档并且判断出那些可用可不用；\",\"没有考虑到可共用贴图和非共用贴图的这方面，白工作两年半了。\",\"资源优化上有做考虑-但忽略了一些我不知道如何使用的贴图，需要补充这方面的短板。\",\"光照模型分析也没有在做一次，对各环节仍有侥幸心理 装懂.. emmm 以后应该懂了\"]},\"532\":{\"c\":[\"shader\"]},\"533\":{\"h\":\"unity-shader-13\"},\"534\":{\"h\":\"特效类大纲\",\"t\":[\"包括透明混合的几种模式、UV顶点动画、绕动等。\",\"透明: \",\"AB;\",\"AD;\",\"AC;\",\"自定义混合方式;\",\"动画: \",\"参数动画\",\"UV动画 \",\"UV流动\",\"UV扰动\",\"序列帧动画\",\"顶点动画 \",\"顶点位置动画\",\"顶点颜色动画\",\"映射: \",\"极坐标\",\"屏幕坐标UV\",\"透明扭曲\"]},\"535\":{\"h\":\"透切 AlphaCutout-AC\",\"t\":[\"用途：\",\"常用于复杂轮廓，明确边缘的物体表现，如：镂空金属，裙摆边缘，特定风格下的头发，树叶，等；\",\"卡通渲染的特效表现； 优点：\",\"没有排序问题； 缺点：\",\"边缘效果太实；\",\"移动端性能较差；\"]},\"536\":{\"h\":\"步骤：\",\"t\":[\"以手写FlatCol（第3课）作为模板，CtrlCV伺候；\",\"修改Shader路径名；\",\"面板参数添加： \",\"_MainTex：主纹理 RGB颜色 A透贴；\",\"_Cutoff：透明剪切阈值；\",\"修改SubShaderTags； \",\"RenderType：修改为对应的TransparentCutout；\",\"ForceNoShadowCasting：设置为True，关闭投影；\",\"IgnoreProjector：设置为True，不响应投射器；\",\"对应声明输入参数；\",\"输入，输出，顶点Shader，增加UV相关代码；\",\"像素Shader： \",\"对主纹理采样，RGB颜色 A透贴；\",\"基于透贴灰度和透切阈值，产生透明效果；\",\"返回值；\",\"::: code-tabs#shell\",\"@tab L13_AC.shader\",\" Shader \\\"AP1/L13/L13_AC\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _MainTex (\\\"RGB：颜色 A：透贴\\\", 2d) = \\\"gray\\\"{} //主纹理 RGB颜色 A透贴； _Cutoff (\\\"透切阈值\\\", range(0.0, 1.0)) = 0.5 //透明剪切阈值 } SubShader { Tags { \\\"RenderType\\\"=\\\"TransparentCutout\\\" // 对应改为Cutout RenderType 还可以玩鹰眼效果 \\\"ForceNoShadowCasting\\\"=\\\"True\\\" // 关闭阴影投射 \\\"IgnoreProjector\\\"=\\\"True\\\" // 不响应投射器 } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform sampler2D _MainTex; uniform float4 _MainTex_ST; uniform half _Cutoff; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>CS o.uv = TRANSFORM_TEX(v.uv, _MainTex); // UV信息 支持TilingOffset return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { half4 var_MainTex = tex2D(_MainTex, i.uv); // 采样贴图 RGB颜色 A透贴 clip(var_MainTex.a - _Cutoff); // 透明剪切 return var_MainTex; // 返回值 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"537\":{\"h\":\"透混 AlphaBland-AB\",\"t\":[\"用途：\",\"常用于复杂轮廓，无明确边缘的物体表现；\",\"常用于半透明的物体表现；\",\"一般的特效表现，打底用； 优点：\",\"移动端性能较好；\",\"边缘效果较好 缺点：\",\"有排序问题；\"]},\"538\":{\"h\":\"步骤：\",\"t\":[\"以AC作为模板，CtrlCV伺候；\",\"修改Shader路径名；\",\"面板参数保留： \",\"_MainTex：主纹理 RGB颜色 A透贴；\",\"修改SubShaderTags； \",\"Queue：渲染队列修改为对应的Transparent；\",\"RenderType：修改为对应的Transparent；\",\"ForceNoShadowCasting：设置为True，关闭投影；\",\"IgnoreProjector：设置为True，不响应投射器；\",\"修改混合方式：Blend One/SrcAlpha OneMinusSrcAlpha；\",\"对应声明输入参数；\",\"输入，输出，顶点Shader，不用改；\",\"像素Shader： \",\"对主纹理采样，RGB颜色 A透贴；\",\"返回值；\",\"::: code-tabs#shell\",\"@tab L13_AB.shader\",\" Shader \\\"AP1/L13/L13_AB\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _MainTex (\\\"RGB：颜色 A：透贴\\\", 2d) = \\\"gray\\\"{} //主纹理 RGB颜色 A透贴； } SubShader { Tags { \\\"Queue\\\"=\\\"Transparent\\\" // 调整渲染顺序 \\\"RenderType\\\"=\\\"TransparentCutout\\\" // 对应改为Cutout RenderType 还可以玩鹰眼效果 \\\"ForceNoShadowCasting\\\"=\\\"True\\\" // 关闭阴影投射 \\\"IgnoreProjector\\\"=\\\"True\\\" // 不响应投射器 } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } Cull Off Blend One OneMinusSrcAlpha // 修改混合方式One/SrcAlpha OneMinusSrcAlpha CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform sampler2D _MainTex; uniform float4 _MainTex_ST; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>CS o.uv = TRANSFORM_TEX(v.uv, _MainTex); // UV信息 支持TilingOffset return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { half4 var_MainTex = tex2D(_MainTex, i.uv); // 采样贴图 RGB颜色 A透贴 return var_MainTex; // 返回值 } ENDCG } } FallBack \\\"Diffuse/\\\" } \",\":::\"]},\"539\":{\"h\":\"透叠 Addtive-AD\",\"t\":[\"用途：\",\"常用于发光体，辉光的表现；\",\"一般的特效表现，提亮用； 问题：\",\"有排序问题；\",\"多层叠加容易堆爆性能(OverDraw)；\",\"作为辉光效果，通常可用后处理代替；\"]},\"540\":{\"h\":\"步骤：\",\"t\":[\"以AB作为模板，CtrlCV伺候；\",\"修改Shader路径名；\",\"面板参数，不用改；\",\"SubShaderTags，不用改；\",\"混合模式修改为：Blend One One；\",\"输入参数，不用改；\",\"输入，输出，顶点Shader，不用改；\",\"像素Shader： \",\"对主纹理采样，RGB颜色即可；\",\"返回值；\",\"::: code-tabs#shell\",\"@tab L13_AD.shader\",\" Shader \\\"AP1/L13/L13_AD\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _MainTex (\\\"RGB：颜色 A：透贴\\\", 2d) = \\\"gray\\\"{} //主纹理 RGB颜色 A透贴； } SubShader { Tags { \\\"Queue\\\"=\\\"Transparent\\\" // 调整渲染顺序 \\\"RenderType\\\"=\\\"TransparentCutout\\\" // 对应改为Cutout RenderType 还可以玩鹰眼效果 \\\"ForceNoShadowCasting\\\"=\\\"True\\\" // 关闭阴影投射 \\\"IgnoreProjector\\\"=\\\"True\\\" // 不响应投射器 } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } Cull Off Blend One One //修改混合方式 CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform sampler2D _MainTex; uniform float4 _MainTex_ST; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>CS o.uv = TRANSFORM_TEX(v.uv, _MainTex); // UV信息 支持TilingOffset return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { half3 var_MainTex = tex2D(_MainTex, i.uv).rgb; // 采样贴图 RGB颜色 A透贴不必须 return half4(var_MainTex, 1.0); // 返回值 } ENDCG } } FallBack \\\"Diffuse/\\\" } \",\":::\"]},\"541\":{\"h\":\"混合模式\",\"t\":[\"AB，AC，AD只是非常多种混合模式中常用的几种；足够 应付一般项目的特效需求；\",\"不排除某些风格特殊，或者特殊用途的场景需要用到其他 混合模式；一般我们提供工具给美术做探索；\",\"探索完毕之后还是要收敛方案，更少的美术选项可让美术 流程更可控；\"]},\"542\":{\"h\":\"混合原理\",\"t\":[\"公式如下：\",\"Src*SrcFactor op Dst*DstFactor\",\"Src：源，当前Shader绘制的结果；\",\"Dst：目标，当前Shader绘制前的背景；\",\"SrcFactor：源乘子，多种形式如下表；\",\"DstFactor：目标乘子，多种形式如下表；\",\"Op：混合运算符，多种形式如右表；\"]},\"543\":{\"h\":\"BlendFactor:\",\"t\":[\"BlendFactor\",\"Description\",\"Zero\",\"Blend factor is (0, 0, 0, 0).\",\"One\",\"Blend factor is (1, 1, 1, 1).\",\"DstColor\",\"Blend factor is (Rd, Gd, Bd, Ad).\",\"SrcColor\",\"Blend factor is (Rs, Gs, Bs, As).\",\"OneMinusDstColor\",\"Blend factor is (1 - Rd, 1 - Gd, 1 - Bd, 1 - Ad);\",\"SrcAlpha\",\"Blend factor is (As, As, As, As).\",\"OneMinusSrcColor\",\"Blend factor is (1 - Rs, 1 - Gs, 1 - Bs, 1 - As).\",\"DstAlpha\",\"Blend factor is (Ad, Ad, Ad, Ad).\",\"OneMinusDstAlpha\",\"Blend factor is (1 - Ad, 1 - Ad, 1 - Ad, 1 - Ad).\",\"SrcAlphaSaturate\",\"Blend factor is (f, f, f, 1); where f = min(As, 1 - Ad).\",\"OneMinusSrcAlpha\",\"Blend factor is (1 - As, 1 - As, 1 - As, 1 - As).\"]},\"544\":{\"h\":\"BlendOp:\",\"t\":[\"BlendOp\",\"Description\",\"Add\",\"Addition (s + d)\",\"Subtract\",\"Subtraction (s - d)\",\"ReverseSubtract\",\"Reverse subtraction (d - s)\",\"Min\",\"Minimum\",\"Max\",\"Maximum\",\"Multiply\",\"Multiply (Advanced OpenGL Blending)\",\"Screen\",\"Screen (Advanced OpenGL Blending)\",\"Overlay\",\"Overlay (Advanced OpenGL Blending)\",\"Darken\",\"Darken (Advanced OpenGL Blending)\",\"Lighten\",\"Lighten (Advanced OpenGL Blending)\",\"ColorDodge\",\"Color dodge (Advanced OpenGL Blending)\",\"ColorBurn\",\"Color burn (Advanced OpenGL Blending)\",\"HardLight\",\"Hard light (Advanced OpenGL Blending)\",\"SoftLight\",\"Soft light (Advanced OpenGL Blending)\",\"Difference\",\"Difference (Advanced OpenGL Blending)\",\"Exclusion\",\"Exclusion (Advanced OpenGL Blending)\",\"HSLHue\",\"HSL Hue (Advanced OpenGL Blending)\",\"HSLSaturation\",\"HSL saturation (Advanced OpenGL Blending)\",\"HSLColor\",\"Hsl color (Advanced OpenGL Blending)\",\"HSLLuminosity\",\"HSLLuminosity (Advanced OpenGL Blending)\"]},\"545\":{\"h\":\"美术自定义混合面板\"},\"546\":{\"h\":\"步骤：\",\"t\":[\"以AB作为模板，CtrlCV伺候；\",\"修改Shader路径名；\",\"面板参数：\",\"_MainTex：主纹理 RGB颜色 A透贴；\",\"_BlendSrc：混合源乘子；\",\"_BlendDst：混合目标乘子；\",\"_BlendOp：混合算符；\",\"SubShaderTags，不用改；\",\"混合算符修改为：BlendOp [BlendOp]\",\"混合方式修改为：Blend [BlendSrc] [BlendDst]\",\"输入，输出，顶点Shader，不用改；\",\"像素Shader，不用改；\",\"::: code-tabs#shell\",\"@tab L13_BlandMode.shader\",\" Shader \\\"AP1/L13/L13_BlandMode\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _MainTex (\\\"RGB：颜色 A：透贴\\\", 2d) = \\\"gray\\\"{} //主纹理 RGB颜色 A透贴； [Enum(UnityEngine.Rendering.BlendMode)] _BlendSrc (\\\"混合源乘子\\\", int) = 0 [Enum(UnityEngine.Rendering.BlendMode)] _BlendDst (\\\"混合目标乘子\\\", int) = 0 [Enum(UnityEngine.Rendering.BlendOp)] _BlendOp (\\\"混合算符\\\", int) = 0 } SubShader { Tags { \\\"Queue\\\"=\\\"Transparent\\\" // 调整渲染顺序 \\\"RenderType\\\"=\\\"TransparentCutout\\\" // 对应改为Cutout RenderType 还可以玩鹰眼效果 \\\"ForceNoShadowCasting\\\"=\\\"True\\\" // 关闭阴影投射 \\\"IgnoreProjector\\\"=\\\"True\\\" // 不响应投射器 } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } Cull Off BlendOp [_BlendOp] // 可自定义混合算符 Blend [_BlendSrc] [_BlendDst] // 可自定义混合模式 CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform sampler2D _MainTex; uniform float4 _MainTex_ST; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 顶点位置 总是必要 float2 uv : TEXCOORD0; // UV信息 采样贴图用 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>CS o.uv = TRANSFORM_TEX(v.uv, _MainTex); // UV信息 支持TilingOffset return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { half4 var_MainTex = tex2D(_MainTex, i.uv); // 采样贴图 RGB颜色 A透贴 return var_MainTex; // 返回值 } ENDCG } } FallBack \\\"Diffuse/\\\" } \",\":::\"]},\"547\":{\"h\":\"常用模式\",\"t\":[\"公式\",\"模式\",\"Blend SrcAlpha OneMinusSrcAlpha\",\"//Normal\",\"Blend OneMinusDstColor One\",\"//Soft Additive\",\"Blend DstColor Zero\",\"// Multiply\",\"Blend DstColor SrcColor\",\"//2x Multiply\",\"Blend op Min\",\"// Darken\",\"Blend One One\",\"Blend op Max\",\"// Lighten\",\"Blend One One\",\"Blend OneMinusDstColor One\",\"//Screen\",\"Blend One OneMinusSrcColor\",\"//Same as above\",\"Blend One One\",\"// Linear Dodge\"]},\"548\":{\"c\":[\"shader\"]},\"549\":{\"h\":\"unity-shader-14\",\"t\":[\"摘要： ing...\",\"::: code-tabs#shell\",\"@tab test\",\"test \",\":::\"]},\"550\":{\"c\":[\"shader\"]},\"551\":{\"h\":\"unity-shader-02\",\"t\":[\"摘要：2HighLight、Hatching、Halftone\",\"上节课答案：\",\"Tips： mip map 指的是根据屏幕距离生成的等比缩小的原图-类似LOD \"]},\"552\":{\"h\":\"规范\",\"t\":[\"为美术团队规范，整理项目资源，是TA的职责之一；所以要从规范自身做起。\",\"规范：\",\"路径规范\",\"命名规范\",\"递交形式规范\",\"shader路径设置 和 材质shader路径选择\"]},\"553\":{\"h\":\"Shader\"},\"554\":{\"h\":\"偏移用加减-缩放用乘除\",\"t\":[\"在开始之前，得提起一个口诀：偏移用加减-缩放用乘除，接下来会用到加减乘除较多。\"]},\"555\":{\"h\":\"2HighLight\",\"t\":[\"两个高光实现的方法-是通过对法线的偏移实现的。然后通过if把值给限定，得到高光mask。\"]},\"556\":{\"h\":\"RampTex + lambert\"},\"557\":{\"h\":\"Offce + nDir\"},\"558\":{\"h\":\"Lerp + fresnel\",\"t\":[\"然后通过开放参数在材质球上调整偏移+颜色等即可实现。\"]},\"559\":{\"h\":\"Hatching\",\"t\":[\"主要节点：\",\"实现方法\",\"绘制一张贴图，通过对屏幕空间正面叠底\",\"然后通过step节点, 取绝对值，得到阴影线，然后再使用lrep节点叠加颜色。最后则是add 相加一下lambert明暗信息-增加体积感。\",\"tips：可以同过使用slide节点对line width（描边宽度）进行控制-这样材质面板中就会暴露出一个滑块，比填数值好用。\"]},\"560\":{\"h\":\"Halftone\",\"t\":[\"首先-我们需要知道，屏幕空间坐标U和V方向的是这样的。然后我们通过对屏幕空间UV的相乘，大于0的值不断被叠加，那么白色的区域占比会越来越多。\",\"然后我们再对这个屏幕空间这个值，取小数。 就会得到这么一个循环，然后U和V通道合并就等于得到了一个tiling的UV坐标。\",\"接下来对它进行一个从映射-也就对当前的局部uv坐标进行偏移（如下说明一样），然后在进行取0-1的长度，就能得到一个点阵一样的程序纹理了。\",\"然后创建明暗信息（lambert），然后对明暗进行重映射\",\"然后在使用power，对小数不断相乘-会越来越小-也就是说会越来越黑。 例如：0.1*0.1=0.01 最后使用Round （四舍五入）取整数，黑的越黑白的越白。\"]},\"561\":{\"c\":[\"shader\"]},\"562\":{\"h\":\"unity-shader-03\",\"t\":[\"摘要：回顾渲染流程，尝试vs写shader，以及一些开放参数的了解。\"]},\"563\":{\"h\":\"回顾渲染流程\",\"t\":[\"以美术绘画的角度理解渲染管线，比较形象。\",\"渲染阶段\",\"比喻角度\",\"模型\",\">\",\"石膏几何体 （看到现实中3d的物体）\",\"输入结构\",\">\",\"几何体的基本几何构成 （脑海里得到的信息）\",\"顶点shader\",\">\",\"通过透视理论加脑海里得到的信息 （在纸上画出基本大型）\",\"输出结构\",\">\",\"通过顶点信息得到面向信息-也就是面的朝向 （在脑海里得知几何体的面朝向）\",\"像素shader\",\">\",\"通过环境的灯光反射信息等-给每个面赋予素描关系（给在纸面上的大型添加素描关系）\",\"渲染结果\",\">\",\"后处理就是 对前面的效果进行一点调整 （例如画完后习惯性用纸笔或者手胡一下）\"]},\"564\":{\"h\":\"尝试shader代码\",\"t\":[\"首先是关联Unity里关联到VScode， 以shader forge新建基础的shader来识别代码，去看unity的基本代码结构。\"]},\"565\":{\"h\":\"1.untiy关联VScode\",\"t\":[\"这样双击任何一个shader就可以跳转到vscode里进行查看与编辑代码\"]},\"566\":{\"h\":\"2.了解简单的shader代码结构\",\"t\":[\"建一个最简的shader模板\",\"改名设置shader路径\",\"无开放参数\",\"Light Count（光照计算）- Single Directional (单个方向光-太阳光）\",\"几何体-Normal Quality - interpolated（归一化）\",\"了解语句结构\"]},\"567\":{\"h\":\"逐句分析\",\"t\":[\"// 定义顶点输入结构体，包含一个四维向量作为顶点位置信息 struct VertexInput { float4 vertex : POSITION; }; // 定义顶点输出结构体，包含一个四维向量用于屏幕空间位置（SV_POSITION表示这是最终位置） struct VertexOutput { float4 pos : SV_POSITION; }; // 顶点着色器函数：将对象空间中的顶点坐标转换到屏幕空间，并返回VertexOutput类型数据 VertexOutput vert(VertexInput v) { // 创建一个VertexOutput类型变量o，并初始化为空值 VertexOutput o = (VertexOutput)0; // 将输入的vertex通过UnityObjectToClipPos函数转换到屏幕空间，并赋值给o.pos o.pos = UnityObjectToClipPos(v.vertex); return o; // 返回处理后的结果数据 } // 片元着色器函数：确定每个像素显示什么颜色 float4 frag(VertexOutpu i) : COLOR { float3 emissive = float3(1, 0.46, 0); // 设置发光属性RGB值 float3 finalColor = emissive; // finalColor等于发光属性 return float4(finalColor,1.0); // 返回计算得到的finalColor（emissive），及alpha=1 } \",\"根据下图创建一个shader的文件-并且复制一个基本shader结构，简单修改返回值， 更改 return float4 的颜色信息。\"]},\"568\":{\"h\":\"3.通过代码-实现 lambert 效果\",\"t\":[\"与shaderforge代码对比-自己手敲的会减少一些无用的代码。\",\"这里讲到unity存光照位置的时候是个齐次坐标，0代表向量，1代表点 _WorldSpaceLightPos0.w为0，表示该光源为平行光。 _WorldSpaceLightPos0.w为1。则表示光源为点光源或聚光灯\"]},\"569\":{\"h\":\"节点组-开放参数\",\"t\":[\"常用的开放参数：\",\"texture - 贴图\",\"vecotr - 向量\",\"silider - 滑块\",\"switch - 开关\",\"color - 颜色\"]},\"570\":{\"h\":\"课后作业\",\"t\":[\"作业：\",\"ps：作业要求是只在暗部进行明暗的变化，尝试通过if去获取mask指控制暗部的范围然后再 通过add叠加，或者blend叠加，看起来都不太理想， 会全局进行明度变化，并不只是在暗部进行明暗的变化。\"]},\"571\":{\"c\":[\"shader\"]},\"572\":{\"h\":\"unity-shader-04\",\"t\":[\"摘要：上节课答案，客评以及了解预积分，和TA专业向\"]},\"573\":{\"h\":\"上节课答案：\",\"t\":[\"SSS材质是Sub-Surface-Scattering的简写，是指光线在物体内部的色散而呈现的半透明效果\",\"法线、世界法线-取绿通道， 会得到一个上下的关系，可以处理直接得到上或下的mask。\"]},\"574\":{\"h\":\"客评\",\"t\":[\"收录一下自己需要了解的节点实现方式\"]},\"575\":{\"h\":\"halfDirection-半方向\",\"t\":[\"float3 halfDirection = normalize(viewDirection+lightDirection); \"]},\"576\":{\"h\":\"不规则边缘mask实现方式：\",\"t\":[\"使用mask（这个mask需要相对灰一些） 和 光照模型 - Blend（Overlay），然后再Round（四舍五入）赋予亮面和暗面的颜色\",\"这里使用到了halfDirection，效果就是会有点跟随摄像机视角的光照，halfDirection的算法就是取摄像机的方向和光照方向相乘\"]},\"577\":{\"h\":\"闲聊\",\"t\":[\"预积分皮肤\",\"TA专业向\"]},\"578\":{\"c\":[\"shader\"]},\"579\":{\"h\":\"unity-shader-05\",\"t\":[\"摘要：Diffuse / Specular Reflection / Phong / BlinnPhong\"]},\"580\":{\"h\":\"Diffuse / Specular Reflection\",\"t\":[\"漫反射和镜面反射的区别就在于微表面的粗糙程度。\",\"Specular Reflection 镜面反射\",\"有目的，沿反射方向，不均匀的反射；\",\"追求漫反射的列子：化妆镜，车漆；\",\"Diffuse Reflection 漫反射\",\"漫无目的，四面八方，均匀的反射；\",\"追求漫反射的列子：电影荧幕；\"]},\"581\":{\"h\":\"各种向量\"},\"582\":{\"h\":\"常用向量：（全要记）\",\"t\":[\"nDir：法线方向，点乘操作时简称n；\",\"lDir：光照方向，点乘操作时简称l；\",\"vDir：观察方向，点乘操作时简称v；\",\"rDir：光反射方向，点乘操作时简称r；\",\"hDir：半角方向(Halfway)，lDir和vDir的中间角方向，点乘操作时简称h；\"]},\"583\":{\"h\":\"所在空间：（暂时只记WS，其余看热闹）\",\"t\":[\"OS：ObjectSpace 物体空间，本地空间；\",\"WS：WorldSpace 世界空间；\",\"VS： ViewSpace 观察空间；\",\"CS：HomogenousClipSpace 齐次剪裁空间；\",\"TS：TangentSpace 切线空间；\",\"TXS：TextureSpace 纹理空间； 例：nDirWS： 世界空间下的法线方向；\"]},\"584\":{\"h\":\"漫反射-Diffuse:\",\"t\":[\"因其向四面八方均匀散射，所以反射亮度和观察者看的方向无关；\",\"实现方式：Lambert（n dot l），显然vDir不参与计算；\"]},\"585\":{\"h\":\"镜面反射-Specular:\",\"t\":[\"镜面反射的反射范围是类似一个圆锥范围-只有在圆锥范围内才能观察到镜面反射的反弹光。\",\"因其反射有明显方向性，所以观察者的视角决定了反射光线的有无，明暗；\",\"实现方式： \",\"Phong（r dot v），即光反射方向和视角方向越重合，反射越强；\",\"Blinn-Phong（n dot h），即法线方向和半角方向越重合，反射越强；\"]},\"586\":{\"h\":\"Phong\"},\"587\":{\"h\":\"Reflect （反射）\",\"t\":[\"输出入射向量 [I] 的反射向量，就像在具有法线 [N] 的表面上反射/反弹一样\"]},\"588\":{\"h\":\"Blinn-Phong\",\"t\":[\"相对phong ，blinn-phong的计算更便宜些。对于现在cpu来说，可忽略不计,以美术为目的。\"]},\"589\":{\"h\":\"vs写一个Lambert+BlinnPhong的shader\"},\"590\":{\"h\":\"vs Code\",\"t\":[\"::: code-tabs#shell\",\"@tab blinn phong\",\" Shader \\\"AP1/L05/L05_OldShool_VS\\\" { // 材质面板参数 Properties { //暴露参数 _MainCol (\\\"颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _SpecularPow (\\\"高光次幂\\\", range(1,50)) = 30 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 // 修饰字（满足小朋友太多的问号,保全发量的大家看热闹,其实不写好像也闹太套） // uniform 共享于vert,frag // attibute 仅用于vert // varying 用于vert,frag传数据 uniform float3 _MainCol; // RGB够了 float3 uniform float _SpecularPow; // 标量 float // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 }; // 输出结构 struct VertexOutput { float4 posCS : SV_POSITION; //裁剪空间（暂理解为屏幕空间吧）顶点位置- 由模型顶点信息换算而来的顶点屏幕位置 float4 posWS : TEXCOORD0; //世界空间顶点位置 float3 nDirWS : TEXCOORD1; //世界空间法线方向-由模型法线信息换算来的世界空间法线信息 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.posCS = UnityObjectToClipPos( v.vertex ); // 变换顶点位置 OS>CS -变换顶点信息 并将其塞给输出结构 o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线方向 OS>WS -变换法线信息 并将其塞给输出结构 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备所有要用到的向量，不会计算去SF拆 float3 nDir = i.nDirWS; float3 lDir = _WorldSpaceLightPos0.xyz; float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float3 hDir = normalize(vDir + lDir ); //准备所有要用到的中间数据，这里是两个点积结果； float nDotl = dot(nDir, lDir); float nDoth = dot(nDir, hDir); //编写光照模型 float lambert = max(0.0, nDotl); float blinnPhong = pow(max(0.0,nDoth),_SpecularPow); float3 finalRGB = _MainCol* lambert+ blinnPhong; //返回结果 return float4(finalRGB, 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\"@tab phong\",\" Shader \\\"AP1/L05/L05_Phong_VS\\\" { // 材质面板参数 Properties { //暴露参数 _MainCol (\\\"颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _MainCol2 (\\\"高光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _SpecularPow (\\\"高光次幂\\\", range(1,50)) = 30 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float3 _MainCol2; // RGB float3 uniform float3 _MainCol; // RGB float3 uniform float _SpecularPow; // 标量 float // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 }; // 输出结构 struct VertexOutput { float4 posCS : SV_POSITION; //裁剪空间（暂理解为屏幕空间吧）顶点位置- 由模型顶点信息换算而来的顶点屏幕位置 float4 posWS : TEXCOORD0; //世界空间顶点位置 float3 nDirWS : TEXCOORD1; //世界空间法线方向-由模型法线信息换算来的世界空间法线信息 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.posCS = UnityObjectToClipPos( v.vertex ); // 变换顶点位置 OS>CS -变换顶点信息 并将其塞给输出结构 o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线方向 OS>WS -变换法线信息 并将其塞给输出结构 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备所有要用到的向量 float3 nDir = i.nDirWS; float3 lDir = _WorldSpaceLightPos0.xyz; float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float3 vRDir = reflect( -vDir, nDir ); //准备所有要用到的中间数据，这里是两个点积结果； float nDotl = dot(nDir, lDir); //得到lambert float vRDotl = dot(vRDir, lDir); //得到phong //编写光照模型 float lambert = max(0.0, nDotl); float Phong = pow(max(0.0,vRDotl),_SpecularPow); float3 finalRGB = (_MainCol* lambert) + (Phong * _MainCol2); //返回结果 return float4(finalRGB, 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"591\":{\"h\":\"作业：\"},\"592\":{\"c\":[\"shader\"]},\"593\":{\"h\":\"unity-shader-06\",\"t\":[\"摘要：作业点评、BRDF\"]},\"594\":{\"h\":\"上节课答案：\"},\"595\":{\"h\":\"FakeEnvReflect\"},\"596\":{\"h\":\"FakeEnvReflect 改\",\"t\":[\"添加第二高光\"]},\"597\":{\"h\":\"green grape 改\",\"t\":[\"删改了一些节点\"]},\"598\":{\"h\":\"Metal_rust 改\",\"t\":[\"删改了一些节点\"]},\"599\":{\"h\":\"BRDF 双向反射分布函数\",\"t\":[\"简而言之就是 把BRDF理解为一个空间-这个空间里描述了反射光的分布。\"]},\"600\":{\"h\":\"各种光照模型\"},\"601\":{\"h\":\"BRDF Explorer\",\"t\":[\"一款迪士尼的开源BRDF的浏览器，可以以直观的3D模型模拟光的反射\",\"新下载地址：https://github.com/wdas/brdf\"]},\"602\":{\"c\":[\"shader\"]},\"603\":{\"h\":\"unity-shader-07\",\"t\":[\"摘要：ColAmbient、3ColAmbient、shadow、 光照模型组合\"]},\"604\":{\"h\":\"ColAmbient\",\"t\":[\"环境光加上环境光遮蔽 - PS：AO叠加个颜色\"]},\"605\":{\"h\":\"3ColAmbient\",\"t\":[\"这应该叫三阶环境光,取Normal的G通道-在分别获取mask，然后与AO混合。\",\"改进-下方是基于取Normal的GB通道进行add-取mask，然后与ao混合，在美术上就是与ao的实际灰阶进行混合。\"]},\"606\":{\"h\":\"3ColAmbient VScood\",\"t\":[\"步骤：\",\"以手写Lambert （第3课）作为模板，复制粘贴大法；\",\"修改Shader路径名；\",\"定义面板参数： \",\"贴图参数的定义方法：_XXX (“面板标签”, 2d) = “white” {} \",\"“white”{} 代表缺省纹理为纯白贴图，其他还有“black”{}“gray”{} …\",\"对应的声明输入参数；\",\"输入结构追加uv0；\",\"输出结构追加uv；\",\"顶点Shader中对uv做赋值；\",\"准备向量 nDir；\",\"通过nDir计算朝上，朝下，侧面各部位遮罩；\",\"通过部位遮罩混合最终环境光颜色；\",\"采样Occlusion图，获得环境遮挡信息；采样贴图方法：tex2D(_Texture, uv)；\",\"用环境遮挡信息对环境光做遮挡；\",\"输出最终颜色。\",\"::: code-tabs#shell\",\"@tab L07_3ColAmbient_VS.shader\",\" Shader \\\"AP1/L07/L07_3ColAmbient_Vs\\\" { // 材质面板参数 Properties { //暴露参数 _Occlusion (\\\"Occlusion\\\", 2d) = \\\"white\\\"{} _EnvUpCol (\\\"EnvUpCol\\\", Color) = (1,1,1,1) _EnvSideCol (\\\"EnvSideCol\\\", Color) = (0.5,0.5,0.5,1) _EnvDownCol (\\\"EnvDownCol\\\", Color) = (0,0,0,1) } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform sampler2D _Occlusion; uniform float3 _EnvUpCol; uniform float3 _EnvSideCol; uniform float3 _EnvDownCol; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 将模型顶点信息输入进来 float4 normal : NORMAL; // 将模型法线信息输入进来 float2 uv0 : TEXCOORD0; // 将模型UV信息输入进来 0通道 共4通道 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 由模型顶点信息换算而来的顶点屏幕位置 float3 nDirWS : TEXCOORD0; // 由模型法线信息换算来的世界空间法线信息 float2 uv : TEXCOORD1; // 追加UV信息用语像素Shader采样贴图 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点信息 并将其塞给输出结构 o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线信息 并将其塞给输出结构 o.uv = v.uv0; // 采集输出贴图 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备所有要用到的向量 float3 nDir = i.nDirWS; // 获取nDir float upMask =max(0.0, nDir.g); // 获取朝上部分遮罩 float downMask =max(0.0, -nDir.g); // 获取朝下部分遮罩 float sideMask =1.0 - upMask - downMask; // 获取侧面部分遮罩 float3 envCol = (_EnvUpCol * upMask ) + (_EnvSideCol * sideMask) + (_EnvDownCol * downMask); // 混合环境色 float occlusion = tex2D (_Occlusion , i.uv); // 采样Occlusion贴图 float3 envLighting = envCol * occlusion; // 计算环境光照 return float4(envLighting , 1.0); // 返回最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } // ps:要主要能复制就复制 不要手打 \",\":::\"]},\"607\":{\"h\":\"shadow VScood\",\"t\":[\"unity 有自带封装好的投影代码\",\"步骤：\",\"以手写FlatCol（第3课）作为模板，复制粘贴大法；\",\"修改Shader路径名；\",\"无需面板参数；\",\"无需声明输入参数；\",\"无需修改输入结构；\",\"输出结构追加：LIGHTING_COORDS(0, 1)：\",\"此为Unity封装好的输出结构内容，照写就行，暂时不看细节；\",\"括号中的参入，如(0, 1)；0，1分别代表占用了TEXCOORD1和TEXCOORD2；\",\"顶点Shader中必须调用Unity封装好的方法：TRANSFER_VERTEX_TO_FRAGMENT(o);\",\"像素Shader中获取投影信息同样通过Unity提供的方法：LIGHT_ATTENUATION(i)；\",\"将投影作为结果输出。\",\"::: code-tabs#shell\",\"@tab L07_shadow_VS.shader\",\" Shader \\\"AP1/L07/L07_shadow_vs\\\" // 自定义Shader路径 { // 材质面板参数 Properties { } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #include \\\"AutoLight.cginc\\\" // 使用Unity投影必须包含这两个库文件 #include \\\"Lighting.cginc\\\" // 同上 #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float _SwitchLambert; // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float3 nDirWS : TEXCOORD1; //由模型法线信息换算来的世界空间法线信息 LIGHTING_COORDS(2,3) //投影用坐标信息 Unity已封装 不用管细节 , 这里COORDS(2,3) 是根据已有的TEXCOORDn坐标添加排列。 例如TEXCOORD1上面有1了那么继续往下排列。 //TEXCOORD是指纹理坐标，float2, float3, float4类型。n是指第几组纹理坐标，maya用户可以理解为UV集 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点信息 并将其塞给输出结构 o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线信息 并将其塞给输出结构 TRANSFER_VERTEX_TO_FRAGMENT(o) // Unity封装 不用管细节 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { float3 nDir = i.nDirWS; // 获取nDir float3 lDir = normalize(_WorldSpaceLightPos0.xyz); // 获取lDir float nDotl = dot(nDir, lDir); // nDir点积lDir float lambert = max(0.0, nDotl); // 截断负值 // return float4 (lambert, lambert, lambert, 1.0); // 输出最终颜色 float shadow = LIGHT_ATTENUATION(i); // 同样Unity封装好的函数 可取出投影 float3 lambertShadow = (lambert * shadow) ; // 多乘了个Lambert return float4 ( lambertShadow , 1.0); // 输出最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"608\":{\"h\":\"简化理解光照构成-光照模型组合\",\"t\":[\"将之前的内容结合\",\"在这里环境的遮挡也可以使用后处理，对整个场景的环境遮蔽进行叠加。\"]},\"609\":{\"h\":\"OldSchoolPlus\"},\"610\":{\"h\":\"作业:\"},\"611\":{\"h\":\"OldSchoolPlus-vs\",\"t\":[\"::: code-tabs#shell\",\"@tab L07_OldSchoolPlus_VS.shader\",\" Shader \\\"AP1/L07/L07_OldSchoolPlus_VS\\\" { // 材质面板参数 Properties { //暴露参数 _MainCol (\\\"baseCol\\\", color) = (1.0, 1.0, 1.0, 1.0) _MainCol2 (\\\"highLight\\\", color) = (1.0, 1.0, 1.0, 1.0) _SpecularPow (\\\"SpecularPow\\\", range(1,50)) = 30 _ShadowPow (\\\"ShadowPow\\\", range(1,50)) = 10 _Occlusion (\\\"AoTex\\\", 2d) = \\\"white\\\"{} _EnvUpCol (\\\"EnvUpCol\\\", Color) = (1,1,1,1) _EnvSideCol (\\\"EnvSideCol\\\", Color) = (0.5,0.5,0.5,1) _EnvDownCol (\\\"EnvDownCol\\\", Color) = (0,0,0,1) } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #include \\\"AutoLight.cginc\\\" // Unity投影库文件 #include \\\"Lighting.cginc\\\" // Unity投影库文件 #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float3 _MainCol2; // RGB float3 uniform float3 _MainCol; // RGB float3 uniform float _SpecularPow; // 标量 float uniform sampler2D _Occlusion; uniform float3 _EnvUpCol; uniform float3 _EnvSideCol; uniform float3 _EnvDownCol; uniform float _ShadowPow ; // add shadow Pow sider // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 float2 uv0 : TEXCOORD0; // 将模型UV信息输入进来 0通道 共4通道 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //裁剪空间（暂理解为屏幕空间吧）顶点位置- 由模型顶点信息换算而来的顶点屏幕位置 float4 posWS : TEXCOORD0; //世界空间顶点位置 float3 nDirWS : TEXCOORD1; //世界空间法线方向-由模型法线信息换算来的世界空间法线信息 LIGHTING_COORDS(2,3) //投影用坐标信息 float2 uv : TEXCOORD4; // 追加UV信息用语像素Shader采样贴图 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点位置 OS>CS -变换顶点信息 并将其塞给输出结构 o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线方向 OS>WS -变换法线信息 并将其塞给输出结构 TRANSFER_VERTEX_TO_FRAGMENT(o) // 投影用Unity封装 o.uv = v.uv0; // 采集输出贴图 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备所有要用到的向量 float3 nDir = i.nDirWS; float3 lDir = _WorldSpaceLightPos0.xyz; float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float shadow = LIGHT_ATTENUATION(i); // 同样Unity封装好的函数 可取出投影 float3 vRDir = reflect( -vDir, nDir ); //准备所有要用到的中间数据，这里是两个点积结果； float nDotl = dot(nDir, lDir); //得到lambert float vRDotl = dot(vRDir, lDir); //得到phong //编写光照模型 float lambert = max(0.0, nDotl); float Phong = pow(max(0.0,vRDotl),_SpecularPow); float3 LambertPhong = (_MainCol* lambert) + (Phong * _MainCol2); //shadow float shadowPow = pow(shadow , _ShadowPow) ; //阴影硬度 float3 shadowPhong = LambertPhong * shadowPow; //3ColAmbient float2 nDirgbadd = (nDir.g+nDir.b); // 取GB的mask float upMask =max(0.0,nDirgbadd); // 获取朝上部分遮罩 float downMask =max(0.0, -nDirgbadd); // 获取朝下部分遮罩 float sideMask =1.0 - upMask - downMask; // 获取侧面部分遮罩 float3 envCol = (_EnvUpCol * upMask ) + (_EnvSideCol * sideMask) + (_EnvDownCol * downMask); // 混合环境色 float occlusion = tex2D (_Occlusion , i.uv); // 采样Occlusion贴图 float3 envLighting = envCol * occlusion; // 计算环境光照 float3 finalCol = envLighting+shadowPhong; //返回结果 return float4(finalCol, 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"612\":{\"c\":[\"shader\"]},\"613\":{\"h\":\"unity-shader-08\",\"t\":[\"摘要：答疑、作业答案、Normal 实现的原理\"]},\"614\":{\"h\":\"答疑：\",\"t\":[\"if判断断开部分Shader语句，有无优化效果？ \",\"没有优化效果，渲染时会执行每一个if分支，一般不写if；\",\"一般用ShaderFeature，MultiCompile；超纲；\"]},\"615\":{\"h\":\"作业答案：\",\"t\":[\"::: code-tabs#shell\",\"@tab L08_OldSchoolPlus_VS.shader\",\"Shader \\\"AP01/L08/L08_OldSchoolPlus_VS\\\" { Properties { _BaseCol (\\\"基本色\\\", Color) = (0.5, 0.5, 0.5, 1.0) _LightCol (\\\"光颜色\\\", Color) = (1.0, 1.0, 1.0, 1.0) _SpecPow (\\\"高光次幂\\\", Range(1, 90)) = 30 _Occlusion (\\\"AO图\\\", 2D) = \\\"white\\\" {} _EnvInt (\\\"环境光强度\\\", Range(0, 1)) = 0.2 _EnvUpCol (\\\"环境天顶颜色\\\", Color) = (1.0, 1.0, 1.0, 1.0) _EnvSideCol (\\\"环境水平颜色\\\", Color) = (0.5, 0.5, 0.5, 1.0) _EnvDownCol (\\\"环境地表颜色\\\", Color) = (0.0, 0.0, 0.0, 0.0) } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" // 追加投影相关包含文件 #include \\\"AutoLight.cginc\\\" #include \\\"Lighting.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float3 _BaseCol; uniform float3 _LightCol; uniform float _SpecPow; uniform sampler2D _Occlusion; uniform float _EnvInt; uniform float3 _EnvUpCol; uniform float3 _EnvSideCol; uniform float3 _EnvDownCol; // 输入结构 struct VertexInput { float4 vertex : POSITION; // 顶点信息 Get✔ float4 normal : NORMAL; // 法线信息 Get✔ float2 uv0 : TEXCOORD0; // UV信息 Get✔ }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; // 裁剪空间（暂理解为屏幕空间吧）顶点位置 float2 uv0 : TEXCOORD0; // UV0 float4 posWS : TEXCOORD1; // 世界空间顶点位置 float3 nDirWS : TEXCOORD2; // 世界空间法线方向 LIGHTING_COORDS(3,4) // 投影相关 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点位置 OS>CS o.uv0 = v.uv0; // 传递UV o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线方向 OS>WS TRANSFER_VERTEX_TO_FRAGMENT(o) // 投影相关 return o; // 返回输出结构 } // 输出结构>>>像素 float4 frag(VertexOutput i) : COLOR { // 准备向量 float3 nDir = normalize(i.nDirWS); float3 lDir = _WorldSpaceLightPos0.xyz; float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float3 rDir = reflect(-lDir, nDir); // 准备点积结果 float ndotl = dot(nDir, lDir); float vdotr = dot(vDir, rDir); // 光照模型(直接光照部分) float shadow = LIGHT_ATTENUATION(i); // 获取投影 float lambert = max(0.0, ndotl); float phong = pow(max(0.0, vdotr), _SpecPow); float3 dirLighting = (_BaseCol * lambert + phong) * _LightCol * shadow; // 光照模型(环境光照部分) float upMask = max(0.0, nDir.g); // 获取朝上部分遮罩 float downMask = max(0.0, -nDir.g); // 获取朝下部分遮罩 float sideMask = 1.0 - upMask - downMask; // 获取侧面部分遮罩 // 混合环境色 float3 envCol = _EnvUpCol * upMask + _EnvSideCol * sideMask + _EnvDownCol * downMask; float occlusion = tex2D(_Occlusion, i.uv0); // 采样Occlusion贴图 float3 envLighting = envCol * _EnvInt * occlusion; // 计算环境光照 // 返回结果 float3 finalRGB = dirLighting + envLighting; return float4(finalRGB, 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"616\":{\"h\":\"作业客评：\",\"t\":[\"收录一下自己需要了解的节点实现方式\"]},\"617\":{\"h\":\"Task1\",\"t\":[\"使用phong 输出合并作为uv再使用rampTex进行采样\",\"取nDir.g 上半部分mask 与相乘 fresnel, 得到朝上的fresnel的mask\",\"使用lerp 表现上半fresnel phong的效果\",\"然后再add 一个lambert\"]},\"618\":{\"h\":\"Task2\",\"t\":[\"使用3ColAmbient + fresnel + ao 做基本的变化\",\"添加 shadow 和 phong-高光\",\"再次添加一个fresnel 模拟sss 做通透些。\"]},\"619\":{\"h\":\"Normal 实现的原理\",\"t\":[\"采样法线贴图并解码；\",\"构建TBN矩阵；\",\"将切线空间下的法线转到世界空间，并归一化；\",\"输出世界空间下的法线信息\",\"简而言之就是把切线空间转化为世界空间，就normal转为worldspaceNomral\",\"::: code-tabs#shell\",\"@tab NomralMapSampler\",\"float3 nDirTS = UnpackNormal(tex2D(normalMap, uv)); float3x3 TBN = float3x3(tDir, bDir, nDir); float3 nDirWS = normalize(mul(nDirTS, TBN)); return nDirWS; \",\":::\"]},\"620\":{\"h\":\"NTB矩阵\",\"t\":[\"TBN矩阵是一种在计算机图形学中常用的矩阵，用于描述曲面局部几何的朝向。TBN矩阵包含了切线（Tangent）、双切线（Bitangent）和法线（Normal）三个基向量，通常用于将纹理坐标从对象空间（Object Space）转换到切线空间（Tangent Space），以便在片元着色器中进行光照计算。通过TBN矩阵，可以实现对物体表面局部细节的精确渲染，提高图形的真实感和逼真度。\"]},\"621\":{\"h\":\"解析\",\"t\":[\"NTB矩阵常见在片元着色器中用于处理法线贴图：\",\"float3 nDirTS = UnpackNormal(tex2D(normalMap, uv))：这行代码通过采样法线贴图（normalMap）来获取纹理坐标（uv）处的法线向量，并将其解压缩成单位长度的三维向量（nDirTS）。\",\"float3x3 TBN = float3x3(tDir, bDir, nDir);：这行代码定义了一个3x3的TBN矩阵，其中包含了切线（tDir）、双切线（bDir）和法线（nDir）向量。\",\"float3 nDirWS = normalize(mul(nDirTS, TBN));：这行代码将纹理空间（TS）中的法线向量（nDirTS）转换为世界空间（WS）中的法线向量（nDirWS）。通过将纹理空间的法线向量与TBN矩阵相乘，并对结果进行归一化，可以将法线向量从纹理空间转换到世界空间，以便进行光照计算等操作。\"]},\"622\":{\"h\":\"Normal VS\",\"t\":[\"代码实现\",\"::: code-tabs#shell\",\"@tab Base-Nomral\",\" Shader \\\"AP1/L08/L08_Nomral_VS\\\" // 定义Shader路径 { // 材质面板参数 Properties { _NormalMap (\\\"NormalMap\\\", 2D) = \\\"black\\\" {} } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform sampler2D _NormalMap; // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 float4 tangent : TANGENT; //模型的tangent信息输入进来 float2 uv0 : TEXCOORD0; //uv }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float2 uv0 : TEXCOORD0; float3 nDirWS : TEXCOORD1; //由模型法线信息换算来的世界空间法线信息 float3 tDirWS : TEXCOORD2; float3 bDirWS : TEXCOORD3; }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点信息 并将其塞给输出结构 o.uv0 = v.uv0; o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线信息 并将其塞给输出结构 o.tDirWS = normalize( mul( unity_ObjectToWorld, float4( v.tangent.xyz, 0.0 ) ).xyz ); o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { // 获取nDir float3 var_NormalMap = UnpackNormal(tex2D(_NormalMap, i.uv0)).rgb; // 采样法线纹理并解码 切线空间nDir float3x3 TBN = float3x3(i.tDirWS, i.bDirWS, i.nDirWS); // 构建TBN矩阵 float3 nDir = normalize(mul(var_NormalMap, TBN)); // 世界空间nDir // 获取lDir float3 lDir = _WorldSpaceLightPos0.xyz; // 一般Lambert float nDotl = dot(nDir, lDir); // nDir点积lDir float lambert = max(0.0, nDotl); // 截断负值 return float4(lambert, lambert, lambert, 1.0); // 输出最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\"@tab Nomral+Phong\",\" Shader \\\"AP1/L08/L08_Nomral_VS\\\" // 定义Shader路径 { // 材质面板参数 Properties { _NormalMap (\\\"NormalMap\\\", 2D) = \\\"black\\\" {} _MainCol2 (\\\"颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _MainCol3 (\\\"高光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _SpecularPow (\\\"高光次幂\\\", range(1,50)) = 30 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform sampler2D _NormalMap; uniform float3 _MainCol; uniform float3 _MainCol2; // RGB float3 uniform float3 _MainCol3; // RGB float3 uniform float _SpecularPow; // 标量 float // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 float4 tangent : TANGENT; //模型的tangent信息输入进来 float2 uv0 : TEXCOORD0; //uv }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float2 uv0 : TEXCOORD0; float3 nDirWS : TEXCOORD1; //由模型法线信息换算来的世界空间法线信息 float3 tDirWS : TEXCOORD2; float3 bDirWS : TEXCOORD3; float4 posWS : TEXCOORD4; //世界空间顶点位置 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点信息 并将其塞给输出结构 o.uv0 = v.uv0; o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线信息 并将其塞给输出结构 o.tDirWS = normalize( mul( unity_ObjectToWorld, float4( v.tangent.xyz, 0.0 ) ).xyz ); o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { // 获取nDir float3 var_NormalMap = UnpackNormal(tex2D(_NormalMap, i.uv0)).rgb; // 采样法线纹理并解码 切线空间nDir float3x3 TBN = float3x3(i.tDirWS, i.bDirWS, i.nDirWS); // 构建TBN矩阵 float3 nDir = normalize(mul(var_NormalMap, TBN)); // 世界空间nDir // 获取lDir float3 lDir = _WorldSpaceLightPos0.xyz; // 获取vDir float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float3 vRDir = reflect( -vDir, nDir ); // 一般Lambert phong float nDotl = dot(nDir, lDir); // nDir点积lDir float lambert = max(0.0, nDotl); // 截断负值 float vRDotl = dot(vRDir, lDir); //得到phong float Phong = pow(max(0.0,vRDotl),_SpecularPow); float3 finalRGB = (_MainCol2* lambert) + (Phong * _MainCol3); return float4(finalRGB, 1.0); // 输出最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"623\":{\"c\":[\"shader\"]},\"624\":{\"h\":\"unity-shader-09\",\"t\":[\"摘要： Fresnel、Matcap、Cubemap、OldSchoolPro\"]},\"625\":{\"h\":\"菲涅尔现象\",\"t\":[\"菲涅尔现象：真实世界中，除了金属之外其它物质，视线垂直于表面时， 反射较弱，而当视线非垂直表面时，夹角越小，反射越明显。\"]},\"626\":{\"h\":\"菲涅尔节点实现\",\"t\":[\"算法：Fresnel = pow(1-ndotv，powVal）\",\"ldotv：理解为光从眼睛发出时的Lambert；中间亮，边缘暗；\",\"1-ldotv：黑白反相，中间暗，边缘亮；\",\"power：套一个power控制边缘亮的范围；\"]},\"627\":{\"h\":\"VScode\",\"t\":[\"::: code-tabs#shell\",\"@tab L09_Fresnel_VS.shader\",\" Shader \\\"AP1/L09/Fresnel_VS\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _fresnel_Pow (\\\"菲尼尔次幂\\\", Range(0, 5)) = 1 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float _fresnel_Pow ; // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float4 posWS : TEXCOORD0; float3 nDirWS : TEXCOORD1; //由模型法线信息换算来的世界空间法线信息 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点信息 并将其塞给输出结构 o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线信息 并将其塞给输出结构 o.posWS = mul(unity_ObjectToWorld, v.vertex); return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备向量 float3 vDirWS = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float3 nDirWS = i.nDirWS; //光照模型 float3 vdotn = (1.0 - dot(vDirWS, nDirWS)); //得到 fresnel float3 finalColor = pow(vdotn, _fresnel_Pow); return float4 (finalColor, 1.0); // 输出最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"628\":{\"h\":\"Matcap\",\"t\":[\"一种无视BRDF，将BRDF渲染结果，用View空间法线朝向，直接映射到模型表面的流氓算法；\",\"常用来模拟环境反射；\",\"一般用于静态预览，就类似zb里的材质球就是Matcap；\"]},\"629\":{\"h\":\"Matcap实现\"},\"630\":{\"h\":\"VsCode\",\"t\":[\"::: code-tabs#shell\",\"@tab L09_Matcap_VS.shader\",\" Shader \\\"AP1/L09/Matcap_vs\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _NormalMap (\\\"法线贴图\\\", 2D) = \\\"bump\\\" {} _Matcap (\\\"Matcap\\\", 2D) = \\\"gray\\\" {} _fresnel_Pow (\\\"菲涅尔次幂\\\", Range(0, 10)) = 1 _EnvSpeclnt (\\\"环境镜面反射强度\\\", Range(0, 5)) = 1 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform sampler2D _NormalMap ; uniform sampler2D _Matcap ; uniform float _fresnel_Pow ; uniform float _EnvSpeclnt ; // 输入结构 = 用到什么 struct VertexInput { float4 vertex : POSITION; //顶点信息 float3 normal : NORMAL; //法线信息 float4 tangent : TANGENT; //切线信息 float2 uv0 : TEXCOORD0; //将模型uv0通道 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float3 posWS : TEXCOORD0; //世界顶点位置 float2 uv0 : TEXCOORD1; //uv信息 float3 nDirWS : TEXCOORD2; //世界法线方向-构建TBN矩阵 float3 tDirWS : TEXCOORD3; //世界切线方向-构建TBN矩阵 float3 bDirWS : TEXCOORD4; //世界副切线方向-构建TBN矩阵 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.uv0 = v.uv0; //传递uv信息 o.pos = UnityObjectToClipPos( v.vertex ); // 顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 法线方向 OS>WS o.tDirWS = normalize(mul(unity_ObjectToWorld, float4(v.tangent.xyz, 0.0)).xyz); //切线方向 OS>WS o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); // 根据nDir tDir求bDir return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //向量准备 float3 nDirTS = UnpackNormal(tex2D(_NormalMap, i.uv0)).rgb; // 采样法线纹理并解码>>切线空间nDir float3x3 TBN = float3x3(i.tDirWS, i.bDirWS, i.nDirWS); // 构建TBN矩阵 float3 nDirWS = normalize(mul(nDirTS, TBN)); // 从切线方向转化为世界法线方向 float3 nDirVS = mul(UNITY_MATRIX_V, float4(nDirWS,0.0)); // ·UNITY_MATRIX_V· unity自带的矩阵v 是将世界法线方向转化为视角方向的矩阵,这个矩阵是四维的，自己手动补齐. float3 vDirWS = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); // 世界摄像机位置 减去 世界屏幕空间位置得到 世界观察方向 //中间量准备 float vdotn = dot(vDirWS, nDirWS); // 准备Fresnel float2 matcapUV = nDirVS.rg * 0.5 + 0.5; // nDirVS remap -1~1 到 0~1 //光照模型 float fresnel = pow(1 - vdotn , _fresnel_Pow); //计算Fresnel float3 matcap = tex2D(_Matcap, matcapUV); //输入matcap贴图 和 uv float3 finalColor = fresnel * matcap *_EnvSpeclnt; // 叠加菲涅尔效果以及环境镜面反射强度 //返回值 return float4 (finalColor, 1.0); // 输出最终颜色 } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"631\":{\"h\":\"Cubemap\"},\"632\":{\"h\":\"Cubemap Tex\",\"t\":[\"获取一张HDR贴图 .exr ，使用PS打开，OpenEXR 读取选项 作为alpha通道\",\"图像大小直接改成需要的正方形的尺寸 - 512x5121024x1024\",\"图像- 模式- 改为 8位/通道\",\"修改为8位通道后 tex会变暗-原因是对tex进行了一次Remap;把最亮的值映射为了1,最暗的值映射为了0.\"]},\"633\":{\"h\":\"unity导入Cubemap 设置\",\"t\":[\"Texture Shape (贴图形状) - Cube；\",\"Mapping (映射) - Latitude-Longitude Layout (Cylindrical)； \",\"Convolution Type(卷积型) - Specular (Glossy Reflection)； //可以理解为min map的显示类型。\",\"Fixup Edge Seams(修复边缘接缝) - yes\",\"Latitude-Longitude Layout (Cylindrical) 纹理包含一个打开的球的图像，这样纬度和经度被映射到水平和垂直维度(就像在地球仪上一样)\",\"Specular (Glossy Reflection) 具有不同平滑度的镜面反射(光滑反射)的卷积立方体贴图。\"]},\"634\":{\"h\":\"Cubemap实现\",\"t\":[\"算法：\",\"将nDir从切线空间转到观察空间；\",\"取RG通道Remap到(0~1)，作为UV对Matcap图采样；\",\"叠加菲涅尔效果，以模拟金属和非金属不同质感；\",\"能用最低成算法实现的,就用底层算法- 避免出错; \",\"one Minus 是 1 - vector 对一个向量进行操作1减去该向量的每个分量，即$1 - \\\\text{vector}$，会导致向量的每个分量被减去原始向量的对应分量，相当于对原始向量的方向进行反转，并且在每个分量上都减去1。这种操作通常用于调整向量的方向，并可能改变其长度。\",\"-1 * vector ，是对一个向量进行乘以-1操作会导致该向量的每个分量都乘以-1，即取其相反方向。这意味着向量的方向将被反转，而其长度将保持不变。这种操作常用于改变光照方向或者反转对象的运动方向，或者在需要对称性变换的情况下使用。\",\"简单理解就是：\",\"乘以-1时，向量的方向会完全反转，但长度不变，就像将箭头从一个方向翻转到另一个方向，但箭头长度不变。\",\"而当你进行1减操作时，向量的每个分量都会减去1，同时方向也反转，就像将箭头向后移动并缩短一段距离。\",\"::: code-tabs#shell\",\"@tab L09_Cubemap_VS.shader\",\" Shader \\\"AP1/L09/Cubemap_VS\\\" // 自定义Shader路径 { // 材质面板参数 Properties { _Cubemap (\\\"CubeMap\\\", Cube) = \\\"_Skybox\\\" {} _CubemapMip (\\\"CubemapMip\\\", Range(0, 7)) = 0 _NormalMap (\\\"NormalMap\\\", 2D) = \\\"bump\\\" {} _FresnelPow (\\\"fresnel_Pow\\\", Range(0, 10)) = 1 _EnvSpecInt (\\\"EnvSpeclnt\\\", Range(0, 5)) = 1 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 //输入参数 uniform samplerCUBE _Cubemap ; uniform sampler2D _NormalMap ; uniform float _FresnelPow ; uniform float _EnvSpecInt ; uniform float _CubemapMip ; // 输入结构 = 用到什么 struct VertexInput { float4 vertex : POSITION; //顶点信息 float3 normal : NORMAL; //法线信息 float4 tangent : TANGENT; //切线信息 float2 uv0 : TEXCOORD0; //将模型uv0通道 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //由模型顶点信息换算而来的顶点屏幕位置 float3 posWS : TEXCOORD0; //世界顶点位置 float2 uv0 : TEXCOORD1; //uv信息 float3 nDirWS : TEXCOORD2; //世界法线方向-构建TBN矩阵 float3 tDirWS : TEXCOORD3; //世界切线方向-构建TBN矩阵 float3 bDirWS : TEXCOORD4; //世界副切线方向-构建TBN矩阵 }; // 输入结构 >>> 顶点shader >>> 输出结构 ，下面是个函数构架 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.uv0 = v.uv0; //传递uv信息 o.pos = UnityObjectToClipPos( v.vertex ); //顶点位置 OS>WS o.posWS = mul(unity_ObjectToWorld, v.vertex); //屏幕世界空间 o.nDirWS = UnityObjectToWorldNormal(v.normal); // 法线方向 OS>WS o.tDirWS = normalize(mul(unity_ObjectToWorld, float4(v.tangent.xyz, 0.0)).xyz); //切线方向 OS>WS o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); // 根据nDir tDir求bDir return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { // 准备向量 float3 nDirTS = UnpackNormal(tex2D(_NormalMap, i.uv0)).rgb; // 采样法线纹理并解码>>切线空间nDir float3x3 TBN = float3x3(i.tDirWS, i.bDirWS, i.nDirWS); // 构建TBN矩阵 float3 nDirWS = normalize(mul(nDirTS, TBN)); // 从切线方向转化为世界法线方向// 计算Fresnel 计算vrDirWS float3 vDirWS = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); // 计算Fresnel float3 vrDirWS = reflect(-vDirWS, nDirWS);// 采样Cubemap // 准备中间变量 float vdotn = dot(vDirWS, nDirWS); // 光照模型 float3 var_Cubemap = texCUBElod(_Cubemap, float4(vrDirWS, _CubemapMip)).rgb; float fresnel = pow(max(0.0, 1.0 - vdotn), _FresnelPow); float3 envSpecLighting = var_Cubemap * fresnel * _EnvSpecInt; // 叠加菲涅尔效果以及环境镜面反射强度 // 返回值 return float4(envSpecLighting, 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"635\":{\"h\":\"作业:\"},\"636\":{\"h\":\"OldSchoolPro\",\"t\":[\"::: code-tabs#shell\",\"@tab testOldSchoolPro_VS.shader\",\" Shader \\\"AP1/L09/L09_OldSchoolPro_VS\\\" { // 材质面板参数 Properties { //暴露参数 [Header(Texture)] _NormalMap (\\\"法线贴图\\\", 2D) = \\\"bump\\\" {} _Occlusion (\\\"AO贴图\\\", 2d) = \\\"white\\\"{} [Header(Diffuse)] _MainCol (\\\"基础颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _MainCol2 (\\\"高光颜色\\\", color) = (1.0, 1.0, 1.0, 1.0) _EnvUpCol (\\\"环境up颜色\\\", Color) = (1,1,1,1) _EnvSideCol (\\\"环境Side颜色\\\", Color) = (0.5,0.5,0.5,1) _EnvDownCol (\\\"环境Down颜色\\\", Color) = (0,0,0,1) [Header(Specular)] _SpecularPow (\\\"高光次幂\\\", Range(10, 90)) = 10 _ShadowPow (\\\"阴影次幂\\\", range(1,50)) = 10 _FresnelPow (\\\"菲妮尔次幂\\\", Range(0, 10)) = 1 _EnvSpecInt (\\\"环境高光强度\\\", Range(0, 5)) = 1 [Header(CubeMap)] _Cubemap (\\\"CubeMap\\\", Cube) = \\\"_Skybox\\\" {} _CubemapMip (\\\"CubemapMip层级\\\", Range(0, 7)) = 0 } SubShader { Tags { \\\"RenderType\\\"=\\\"Opaque\\\" } Pass { Name \\\"FORWARD\\\" Tags { \\\"LightMode\\\"=\\\"ForwardBase\\\" } CGPROGRAM #pragma vertex vert #pragma fragment frag #include \\\"UnityCG.cginc\\\" #include \\\"AutoLight.cginc\\\" // Unity投影库文件 #include \\\"Lighting.cginc\\\" // Unity投影库文件 #pragma multi_compile_fwdbase_fullshadows #pragma target 3.0 // 输入参数 uniform float3 _MainCol2; // RGB float3 uniform float3 _MainCol; // RGB float3 uniform float _SpecularPow; // 标量 float uniform sampler2D _Occlusion; uniform float3 _EnvUpCol; uniform float3 _EnvSideCol; uniform float3 _EnvDownCol; uniform float _ShadowPow ; // add shadow Pow sider uniform samplerCUBE _Cubemap ; uniform sampler2D _NormalMap ; uniform float _FresnelPow ; uniform float _EnvSpecInt ; uniform float _CubemapMip ; // 输入结构 struct VertexInput { float4 vertex : POSITION; //将模型的顶点信息输入进来 float3 normal : NORMAL; //将模型的noraml信息输入进来 float4 tangent : TANGENT; //切线信息 float2 uv0 : TEXCOORD0; // 将模型UV信息输入进来 0通道 共4通道 }; // 输出结构 struct VertexOutput { float4 pos : SV_POSITION; //裁剪空间（暂理解为屏幕空间吧）顶点位置- 由模型顶点信息换算而来的顶点屏幕位置 float4 posWS : TEXCOORD0; //世界空间顶点位置 float2 uv0 : TEXCOORD1; //uv信息 float3 nDirWS : TEXCOORD2; //世界法线方向-构建TBN矩阵 float3 tDirWS : TEXCOORD3; //世界切线方向-构建TBN矩阵 float3 bDirWS : TEXCOORD4; //世界副切线方向-构建TBN矩阵 LIGHTING_COORDS(5,6) //投影用坐标信息 }; // 输入结构>>>顶点Shader>>>输出结构 VertexOutput vert (VertexInput v) { VertexOutput o = (VertexOutput)0; // 新建一个输出结构 o.uv0 = v.uv0; // 采集输出贴图 o.pos = UnityObjectToClipPos( v.vertex ); // 变换顶点位置 OS>CS -变换顶点信息 并将其塞给输出结构 o.posWS = mul(unity_ObjectToWorld, v.vertex); // 变换顶点位置 OS>WS o.nDirWS = UnityObjectToWorldNormal(v.normal); // 变换法线方向 OS>WS -变换法线信息 并将其塞给输出结构 o.tDirWS = normalize(mul(unity_ObjectToWorld, float4(v.tangent.xyz, 0.0)).xyz); //切线方向 OS>WS o.bDirWS = normalize(cross(o.nDirWS, o.tDirWS) * v.tangent.w); // 根据nDir tDir求bDir TRANSFER_VERTEX_TO_FRAGMENT(o) // 投影用Unity封装 return o; // 将输出结构 输出 } // 输出结构 >>> 像素 float4 frag(VertexOutput i) : COLOR { //准备所有要用到的向量 float3 nDirTS = UnpackNormal(tex2D(_NormalMap, i.uv0)).rgb; // 采样法线纹理并解码>>切线空间nDir float3x3 TBN = float3x3(i.tDirWS, i.bDirWS, i.nDirWS); // 构建TBN矩阵 float3 nDirWS = normalize(mul(nDirTS, TBN)); // 从切线方向转化为世界法线方向// 计算Fresnel 计算vrDirWS float3 vDirWS = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); // 计算Fresnel float3 vrDirWS = reflect(-vDirWS, nDirWS);// 采样Cubemap // float3 nDir = i.nDirWS; float3 lDirWS = _WorldSpaceLightPos0.xyz; // float3 vDir = normalize(_WorldSpaceCameraPos.xyz - i.posWS.xyz); float shadow = LIGHT_ATTENUATION(i); // 同样Unity封装好的函数 可取出投影 float3 vRDir = reflect( -vDirWS, nDirWS ); //准备所有要用到的中间数据，这里是两个点积结果； float nDotl = dot(nDirWS, lDirWS); //得到lambert float vRDotl = dot(vRDir, lDirWS); //得到phong float vdotn = dot(vDirWS, nDirWS); //得到视方向，为fresnel准备 //编写光照模型 float lambert = max(0.0, nDotl); float Phong = pow(max(0.0,vRDotl),_SpecularPow); float3 LambertPhong = (_MainCol* lambert) + (_MainCol2 * Phong); //shadow float shadowPow = pow(shadow , _ShadowPow) ; //阴影硬度 float3 shadowPhong = LambertPhong * shadowPow; // //3ColAmbient1 float2 nDirgbadd = (nDirWS.g); // 取GB的mask float upMask =max(0.0, nDirWS.g); // 获取朝上部分遮罩 float downMask =max(0.0, -nDirWS.g); // 获取朝下部分遮罩 float sideMask =1.0 - upMask - downMask; // 获取侧面部分遮罩 float3 envCol = (_EnvUpCol * upMask ) + (_EnvSideCol * sideMask) + (_EnvDownCol * downMask); // 混合环境色 float occlusion = tex2D (_Occlusion , i.uv0); // 采样Occlusion贴图 float3 envLighting = envCol * occlusion; // 计算环境光照 // //3ColAmbient2 是基于物体体质计算的类似AO和实际灰阶进行混合 // float2 nDirgbadd = (nDirWS.g+nDirWS.b); // 取GB的mask // float upMask =max(0.0,nDirgbadd); // 获取朝上部分遮罩 // float downMask =max(0.0, -nDirgbadd); // 获取朝下部分遮罩 // float sideMask =1.0 - upMask - downMask; // 获取侧面部分遮罩 // float3 envCol = (_EnvUpCol * upMask ) + (_EnvSideCol * sideMask) + (_EnvDownCol * downMask); // 混合环境色 // float occlusion = tex2D (_Occlusion , i.uv0); // 采样Occlusion贴图 // float3 envLighting = envCol * occlusion; // 计算环境光照 //Cubemap+fresnel float3 var_Cubemap = texCUBElod(_Cubemap, float4(vrDirWS, _CubemapMip)).rgb; float fresnel = pow(max(0.0, 1.0 - vdotn), _FresnelPow); float3 envSpecLighting = var_Cubemap * fresnel * _EnvSpecInt; // 叠加菲涅尔效果以及环境镜面反射强度 float3 finalCol = envLighting+shadowPhong+envSpecLighting; //返回结果 return float4(finalCol , 1.0); } ENDCG } } FallBack \\\"Diffuse\\\" } \",\":::\"]},\"637\":{\"h\":\"改进连连看\",\"t\":[\"顺便改进了下SF版本-原先lambert的颜色和phong的高光没分开的问题\",\"原版#oldschoolplus \",\"nDir通过TBN矩阵都转换为了nDirWS\",\"那么SF版本的也应该把所有用到法线的地方进行重新连到nDirWS\",\"::: code-tabs#shell\",\"@tab L09_OldSchoolPro_SF.shader\",\"// Shader created with Shader Forge v1.40 // Shader Forge (c) Freya Holmer - http://www.acegikmo.com/shaderforge/ // Note: Manually altering this data may prevent you from opening it in Shader Forge /*SF_DATA;ver:1.40;sub:START;pass:START;ps:flbk:,iptp:0,cusa:False,bamd:0,cgin:,cpap:True,lico:1,lgpr:1,limd:0,spmd:1,trmd:0,grmd:0,uamb:True,mssp:True,bkdf:False,hqlp:False,rprd:False,enco:False,rmgx:True,imps:True,rpth:0,vtps:0,hqsc:True,nrmq:1,nrsp:0,vomd:0,spxs:False,tesm:0,olmd:1,culm:0,bsrc:0,bdst:1,dpts:2,wrdp:True,dith:0,atcv:False,rfrpo:True,rfrpn:Refraction,coma:15,ufog:False,aust:True,igpj:False,qofs:0,qpre:1,rntp:1,fgom:False,fgoc:False,fgod:False,fgor:False,fgmd:0,fgcr:0.5,fgcg:0.5,fgcb:0.5,fgca:1,fgde:0.01,fgrn:0,fgrf:300,stcl:False,atwp:False,stva:128,stmr:255,stmw:25